function sendemail(){var a=$("#nome").val();var b=$("#email").val();var c=$("#assunto").val();var d=$("#mensagem").val();$.ajax({url:"sendemail.php",type:"POST",data:"nome="+a+"&email="+b+"&assunto="+c+"&mensagem="+d,success:function(e){console.log(e);if(e==1){$("#form1")[0].reset();$("#myModal").modal("hide")}else{console.log(e)}},error:function(e,g,f){alert("Status: "+g);alert("Error: "+f);console.log(arguments)}})};